mapper.select:

   SELECT SUM(MONEY) from CLUB_USER_USE  where  
   	     CREATE_TIME  >=  #{morning} 
		and  CREATE_TIME  <=  #{night} and CLUB_ID = #{clubId}

SELECT COUNT(CIRCLE_NUM) FROM CLUB_GAME_ROOM WHERE (USER_ID1 = #{userId} OR USER_ID2 = #{userId} OR
USER_ID3 = #{userId} OR  USER_ID4 = #{userId})  and CREATE_TIME>=#{morning} and CREATE_TIME<#{night}
AND CLUB_ID=#{clubId}
